import React, { Component } from 'react';

class Item extends Component {
  handleClick = () => {
    const { item, onAdd } = this.props;
    if (typeof onAdd === 'function') {
      onAdd(item);
    } else {
      console.error('onAdd is not a function');
    }
  };

  render() {
    const { item } = this.props;

    return (
      <div className='item'>
        <img src={"./img/" + item.img} alt={item.title} />
        <h2>{item.title}</h2>
        <p>{item.desc}</p>
        <b>{item.price}</b>
        <div className='add-to-cart' onClick={this.handleClick}>+</div>
      </div>
    );
  }
}

export default Item;
